stroka=''
counter=0
flag=0
lens=[]
sims=['D','A','F']
for i in range(len(stroka)-3) :
    if stroka[i]==sims[0]:
        if stroka[i+1]==sims[1]:
            if stroka[i+2]==sims[2]:
                counter+=1
                flag=1
    elif stroka[i]==sims[1] and flag==1:
        counter+=1
        flag+=1
    elif stroka[i]==sims[2] and flag==2:
        counter+=1
        flag=3
    elif flag==3 and stroka[i] not in sims:
        flag=0
        lens.append(counter)
        counter=0
print(max(lens))
